        <script>
              // Create
              $(document).ready(function() {
                $("#createLanguageBtn").click(function() {
                  $("#languageList").hide();
                  $("#createLanguageBtn").hide();
                  $("#goToHome").hide();
                  $("#createLanguageForm").show();
                });
              });
              $(document).ready(function() {
                  $("#createLanguageFormSubmit").submit(function(event) {
                    event.preventDefault();
                    let updatedName = $("#updateLanguageForm input[name='name']").val();
                    let updatedISOCode = $("#updateLanguageForm input[name='iso_code']").val();
                    this.submit();
                  });
                });
              $(document).ready(function() {
                $("#cancelCreateLanguageBtn").click(function() {
                  event.preventDefault();
                  $("#createLanguageForm").hide();
                  $("#languageList").show();
                  $("#createLanguageBtn").show();
                  $("#goToHome").show();
                });
              });
                // Update
                $(document).ready(function() {
                $(".editButton").click(function() {
                  $("#createLanguageBtn").hide();
                  $("#goToHome").hide();
                });
              });
                $(document).ready(function() {
                  $("#cancelUpdateLanguageBtn").click(function(event) {
                    event.preventDefault();
                    $("#updateLanguageForm").hide();
                    $("#languageList").show();
                    $("#createLanguageBtn").show();
                    $("#goToHome").show();
                  });
                });
                // Delete
                $(document).ready(function() {
                  $("#deleteLanguageFormSubmit").submit(function(event) {
                    event.preventDefault();
                    $('#confirmDeleteModal').modal('hide');
                    let languageId = $("#deleteId input[name='id']").val();
                    $(this).attr('action', '/Languages');
                    this.submit();
                  });
                });
                $(document).ready(function() {
                  $("#cancelDeleteLanguageBtn").click(function(event) {
                    event.preventDefault();
                  });
                });
                document.addEventListener('DOMContentLoaded', function() {
              let languageTable = document.getElementById('languageTable');
              if (languageTable && languageTable.children.length > 0) {
                languageTable.addEventListener('click', function(event) {
                  if (event.target.classList.contains('editButton')) {
                    let languageId = event.target.dataset.id;
                    let languageName = event.target.dataset.name;
                    let languageISOCode = event.target.dataset.iso_code;
                    document.getElementById('updateLanguageForm').style.display = 'block';
                    document.querySelector('#updateLanguageForm input[name="id"]').value = languageId;
                    document.querySelector('#updateLanguageForm input.updateName').value = languageName;
                    document.querySelector('#updateLanguageForm input.updateISOCode').value = languageISOCode;
                    document.getElementById('languageList').style.display = 'none';
                  }
                  if (event.target.classList.contains('deleteButton')) {
                    let languageId = event.target.dataset.id;
                    let languageName = event.target.dataset.name;
                    let languageNameConfirmation = document.getElementById('languageNameConfirmation');
                    document.querySelector('#confirmDeleteModal input[name="id"]').value = languageId;
                    languageNameConfirmation.textContent = 'Are you sure you want to delete the language "' + languageName + '" and all its relationships?';
                    $('#confirmDeleteModal').modal('show');
                  }
                });
              }
            });
        </script>
        <script>
            function showMessage(messageType, errorType) {
              let messageText = '';
              switch (messageType) {
                case 'success':
                  messageText = 'Language created successfully!';
                  break;
                case 'error':
                  messageText = 'Error creating language. Please try again.';
                  break;
                case 'error_duplicate':
                  messageText = 'The language name already exists. Create failed.';
                  break;
                case 'success_update':
                  messageText = 'Language updated successfully!';
                  break;
                case 'error_update':
                  messageText = 'Error updating language. Please try again.';
                  break;
                case 'error_update_duplicate':
                  messageText = 'The language name already exists. Update failed.';
                  break;
                case 'success_delete':
                  messageText = 'Language deleted successfully!';
                  break;
                case 'error_delete':
                  messageText = 'Error deleting language. Please try again.';
                  break;
                default:
                  break;
              }
              let messageElement = document.createElement('div');
              messageElement.className = 'alert alert-dismissible fade show ' + (messageType.includes('success') ? 'alert-success' : 'alert-danger');
              messageElement.innerHTML = messageText;
              messageElement.style.position = 'fixed';
              messageElement.style.top = '10px';
              messageElement.style.right = '10px';
              messageElement.style.zIndex = '9999';
              document.body.appendChild(messageElement);
              setTimeout(function() {
                messageElement.style.display = 'none';
              }, 3000);
            }
            let messageType = '<?php echo isset($_SESSION["message"]) ? $_SESSION["message"] : ""; ?>';
            if (messageType !== '') {
              showMessage(messageType);
              <?php unset($_SESSION["message"]); ?>
              <?php unset($_SESSION["errors"]); ?>
            }
        </script>
